  <ion-toolbar color="primary">
    <ion-title></ion-title>
    <ion-button fill="clear" slot="start">
      <ion-back-button color="light"></ion-back-button>
    </ion-button>
  </ion-toolbar>
</ion-header>

<ion-content>
  <ion-card>
    <ion-card-header>
      <ion-card-title>Registro</ion-card-title>
    </ion-card-header>
    <ion-card-content>
      <form [formGroup]="formularioregristroad" (ngSubmit)="registrarse()">
        <ion-item fill="solid">
          <ion-label position="floating">Nombre <ion-text color="danger">*</ion-text></ion-label>
          <ion-input placeholder="Ingrese su Nombre" required formControlName="nombre" type="text"></ion-input>
        </ion-item>
        <ion-item><ion-label>
            <hr class="solid">
          </ion-label></ion-item>
        <ion-item fill="solid">
          <ion-label position="floating">Apellido <ion-text color="danger">*</ion-text></ion-label>
          <ion-input placeholder="Ingrese su Apellido" required formControlName="apellido" type="text"></ion-input>
        </ion-item>
        <ion-item><ion-label>
            <hr class="solid">
          </ion-label></ion-item>
        <ion-item fill="solid">
          <ion-label position="floating">Correo electronico <ion-text color="danger">*</ion-text></ion-label>
          <ion-input placeholder="Ingrese su Correo electronico" required formControlName="correo"
            type="text"></ion-input>
        </ion-item>
        <ion-item><ion-label>
            <hr class="solid">
          </ion-label></ion-item>
        <ion-item fill="solid">
          <ion-label position="floating">Contraseña <ion-text color="danger">*</ion-text></ion-label>
          <ion-input placeholder="Ingrese su Contraseña" required formControlName="contraseña" type="password"
            minlength=6></ion-input>
        </ion-item>
        <ion-item fill="solid">
          <ion-label position="floating">Llave <ion-text color="danger">*</ion-text></ion-label>
          <ion-input placeholder="Ingrese su Contraseña" required formControlName="llave" type="password"
            minlength=6></ion-input>
        </ion-item>

        <ion-button block color="primary" type="submit">
          Aceptar
        </ion-button>
      </form>
    </ion-card-content>
  </ion-card>

</ion-content>
