import { Component, OnInit } from '@angular/core';
import {
  ReactiveFormsModule,
  FormGroup,
  FormControl,
  Validators,
  FormBuilder,
} from '@angular/forms';

import { AlertController } from '@ionic/angular';
import { AuthService } from '../services/auth.service';
import { FirestoreService } from '../services/firestore.service';
import { Router } from '@angular/router';
import { paciente } from '../models/interface';

@Component({
  selector: 'app-rpaciente',
  templateUrl: './rpaciente.page.html',
  styleUrls: ['./rpaciente.page.scss'],
})
export class RpacientePage implements OnInit {

  public formularioregistrop: FormGroup;

  newpaciente: paciente= {
    uid: "",
    nombre: "",
    apellido: "",
    cedula: "",
    fnacimiento: "",
    edad: 0,
    correo:"",
    contraseña: "",
    rol: "Paciente",
  }
  

  constructor(private fb: FormBuilder, private alertController: AlertController, 
    private auth: AuthService, private store: FirestoreService, private router: Router) { 
    
    this.formularioregistrop = this.fb.group({
      'nombre': new FormControl("", Validators.required),
      'apellido': new FormControl("", Validators.required),
      'cedula': new FormControl("", Validators.required),
      'fnacimiento': new FormControl("", Validators.required),
      'edad': new FormControl("", Validators.required),
      'correo': new FormControl("", Validators.required),
      'contraseña': new FormControl("", Validators.required)
    })
  }

  async registrarse(){

    var f = this.formularioregistrop.value;

    if (this.formularioregistrop.invalid) {

      const alert = await this.alertController.create({
        header: 'Datos incompletos',
        message: 'Debe de ingresar todos los campos o asegurarse que su contraseña tenga minimo 6 caracteres',
        buttons: ['Aceptar'],
      });

      await alert.present();
      return;
    }

    else{
      
      this.newpaciente.nombre = f.nombre,
      this.newpaciente.apellido= f.apellido,
      this.newpaciente.cedula= f.cedula,
      this.newpaciente.fnacimiento= f.fnacimiento,
      this.newpaciente.edad= f.edad,
      this.newpaciente.correo= f.correo,
      this.newpaciente.contraseña= f.contraseña,
      this.newpaciente.rol= "Paciente",

    console.log(this.newpaciente)

    const res = await this.auth.registrar(this.newpaciente.correo, this.newpaciente.contraseña).catch(res => {
      console.log('error')
    })

    if (res){
      console.log("Usuario creado");
      const path = 'Pacientes'
      const Id = res.user!.uid;
      this.newpaciente.uid = Id;
      this.newpaciente.contraseña = "";

      await this.store.createDoc(this.newpaciente,path,Id)

      const alert2 = await this.alertController.create({
        header: 'Usuario creado',
        buttons: ['INICIO'],
      });

      await alert2.present();
      this.router.navigate(['../login'])

    }


    }

    
  }

  ngOnInit() {
  }

}
<ion-header>
  <ion-toolbar color="primary">
    <ion-title></ion-title>
    <ion-button fill="clear" slot="start">
      <ion-back-button color="light" ></ion-back-button>
    </ion-button>
  </ion-toolbar>
</ion-header>

<ion-content>
  <ion-card>
    <ion-card-header>
      <ion-card-title>Registro</ion-card-title>
    </ion-card-header>
    <ion-card-content>
      <form [formGroup]="formularioregistrop" (ngSubmit)="registrarse()">
        <ion-item fill="solid">
          <ion-label position="floating">Nombre <ion-text color="danger">*</ion-text></ion-label>
          <ion-input placeholder="Ingrese su Nombre" required formControlName="nombre" type="text"></ion-input>
        </ion-item>
        <ion-item><ion-label><hr class="solid"></ion-label></ion-item>
        <ion-item fill="solid">
          <ion-label position="floating">Apellido <ion-text color="danger">*</ion-text></ion-label>
          <ion-input placeholder="Ingrese su Apellido" required formControlName="apellido" type="text"></ion-input>
        </ion-item>
        <ion-item><ion-label><hr class="solid"></ion-label></ion-item>
        <ion-item fill="solid">
          <ion-label position="floating">Cédula<ion-text color="danger">*</ion-text></ion-label>
          <ion-input  placeholder="Ingrese su Numero de Cédula" required formControlName="cedula" type="text"></ion-input>
        </ion-item>
         <ion-item><ion-label><hr class="solid"></ion-label></ion-item>
        <ion-item fill="solid">
          <ion-label position="floating">Edad <ion-text color="danger">*</ion-text></ion-label>
          <ion-input placeholder="Ingrese su Edad" required formControlName="edad" type="number"></ion-input>
        </ion-item>
        <ion-item><ion-label><hr class="solid"></ion-label></ion-item>
        <ion-item>
          <ion-label position="floating">Fecha de nacimiento <ion-text color="danger">*</ion-text></ion-label>
          <ion-input required formControlName="fnacimiento" type="text"></ion-input>
        </ion-item>
        <ion-item><ion-label><hr class="solid"></ion-label></ion-item>
        <ion-item fill="solid">
          <ion-label position="floating">Correo electronico <ion-text color="danger">*</ion-text></ion-label>
          <ion-input placeholder="Ingrese su Correo electronico" required formControlName="correo" type="text"></ion-input>
        </ion-item>
        <ion-item><ion-label><hr class="solid"></ion-label></ion-item>
        <ion-item fill="solid">
          <ion-label position="floating">Contraseña <ion-text color="danger">*</ion-text></ion-label>
          <ion-input placeholder="Ingrese su Contraseña" required formControlName="contraseña" type="password" minlength= 6></ion-input>
        </ion-item>
        
        <ion-button block color="primary" type="submit">
          Aceptar
        </ion-button>
      </form>
    </ion-card-content>
  </ion-card>
</ion-content>
