import { Component, Input, OnInit } from '@angular/core';
import { MenuController } from '@ionic/angular';
import { Reporte } from 'src/app/models/interface';

@Component({
  selector: 'app-reportes',
  templateUrl: './reportes.component.html',
  styleUrls: ['./reportes.component.scss'],
})
export class ReportesComponent implements OnInit {
  @Input()reporte!: Reporte;
  
  constructor(public menuController: MenuController,) { }

  ngOnInit() {}
 
}
<ion-card class="custom-card" >
    <ion-item class="normal"><label class="custom-label">Nombre:</label> {{reporte.nombre}}</ion-item>
    <!-- <ion-item class="pequeña"><label class="custom-label">Identificador:</label> {{reporte.idpaciente}}</ion-item> -->
    <ion-item class="pequeña"><label class="custom-label">Edad:</label> {{reporte.edad}}</ion-item>
    <ion-item class="pequeña"><label class="custom-label">Peso:</label> {{reporte.peso}}</ion-item>
    <ion-item class="pequeña"><label class="custom-label">Altura:</label> {{reporte.altura}}</ion-item>
    <ion-item class="pequeña"><label class="custom-label">Medicamentos:</label> {{reporte.medicamentos}}</ion-item>
    <ion-item class="pequeña"><label class="custom-label">Condición médica:</label> {{reporte.condicion}}</ion-item>
</ion-card>
