import { Component, OnInit, ViewChild } from "@angular/core";
import { doctor, paciente } from "./models/interface";
import { Router } from "@angular/router";
import { AuthService } from "./services/auth.service";
import { FirestoreService } from "./services/firestore.service";
import { IonApp, IonNav, NavController } from "@ionic/angular";

@Component({
  selector: "app-root",
  templateUrl: "app.component.html",
  styleUrls: ["app.component.scss"],
})
export class AppComponent {
  uid = "";
  pacientes: paciente | undefined;
  doctores: doctor | undefined;
  pages: any;
  

  @ViewChild(IonNav)
  nav!: IonNav;

  constructor(
    private auth: AuthService,
    private router: Router,
    private store: FirestoreService,
    public navCtrl: NavController,
  ) {}

  async menu(){
    const uid = await this.auth.getUid();
    if (uid) {
      this.uid = uid;
    }

    const pathp = "Pacientes";
    const pathd = "Doctores";
    this.store.getDoc<paciente>(pathp, this.uid).subscribe((res) => {
      if (res) {
        this.pacientes = res;
      }
    });
    this.store.getDoc<doctor>(pathd, this.uid).subscribe((res) => {
      if (res) {
        this.doctores = res;
      }
    });

    if (this.doctores) {
      console.log("Doctor");

      this.pages = [
        {
          tittle: "set-reportes",
          page: "set-reportes",
          icon: "reader-outline",
        },
        {
          tittle: "lista-reporte",
          page: "lista-reporte",
          icon: "list-outline",
        },
      ];

      this.openPage("tabiniciald");
    }

  }


  openPage(page: string){
    this.router.navigate([page]);

  }

  logout(){
    this.auth.cerrars();
    this.router.navigate(["./login"]);

  }
}
<ion-app>
  <ion-split-pane contentId="main">
    <ion-menu contentId="main" menuId="main">
      <ion-header>
        <ion-toolbar>
          <ion-title class="subtitulo">Menu</ion-title>
        </ion-toolbar>
      </ion-header>
      <ion-content>
        <ion-menu-toggle>
          <ion-list >
            <<ion-button *ngFor="let p on pages" (click)="openPage(p.page)"  expand="block" fill="clear" shape="round">
              <ion-icon slot="start" name="p.icon"></ion-icon>
              {{p.tittle}}
            </ion-button> >
          </ion-list>
          <ion-footer>
            <ion-toolbar>
              <ion-button (click)="logout()">
                <ion-icon slot="start" name="sign-out"></ion-icon>
                Cerrar sesion
              </ion-button>
            </ion-toolbar>
          </ion-footer>

        </ion-menu-toggle>
      </ion-content>

    </ion-menu>
    <ion-router-outlet id="main"></ion-router-outlet>
  </ion-split-pane>

</ion-app>
