import { Component, OnInit } from '@angular/core';
import {
  FormGroup,
  FormControl,
  Validators,
  FormBuilder,
} from '@angular/forms';
import { AuthService } from '../services/auth.service';
import { Router } from '@angular/router';
import { FirestoreService } from '../services/firestore.service';
import { AlertController } from '@ionic/angular';
import { AppComponent } from '../app.component'

@Component({
  selector: 'app-login',
  templateUrl: './login.page.html',
  styleUrls: ['./login.page.scss'],
})
export class LoginPage implements OnInit {

  formulariologin: FormGroup;
  info: {} | undefined;
  

  constructor(private fb: FormBuilder, private auth: AuthService, private router: Router,
    private store: FirestoreService, private alertcontroller: AlertController, private menu: AppComponent) {

    this.formulariologin = this.fb.group({
      'user': new FormControl("", Validators.required),
      'password': new FormControl("", Validators.required)
    })
   }

   async ingresar(){
    var form= this.formulariologin.value;

    const res = await this.auth.login(form.user, form.password);

    if(res){
      console.log("Usuario correcto")
      const Id = res.user?.uid;
      if (Id){
       this.store.getDoc("Pacientes", Id).subscribe(res => { 
          if (res){
          this.info = res;
          if (this.info) {
            console.log(this.info)
            this.router.navigate(['home']);
          }
          
          } 
        });
        this.store.getDoc("Doctores", Id).subscribe(res => { 
          if (res){
          this.info = res;
          if (this.info) {
            console.log(this.info)
            this.router.navigate(['homed']);
          }
          
          } 
        });
      
      
      }  

    }
    
    if(this.formulariologin.invalid){
    console.log("Usuario incorrecto")
    const alert = await this.alertcontroller.create({
        header: 'Contraseña o usuario incorrecto',
        message: 'Asegurese que su contraseña y usuario sean correctos o registrese',
        buttons: ['Aceptar'],
      });

      await alert.present();
      return;
   }
   }
   

  ngOnInit() {
  }

}
<ion-header>
  <ion-toolbar color="principal">
    <ion-title>MedMate</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content>
  <ion-card class="login-card">
    <ion-card-content class="login-card-content">
      <form [formGroup]="formulariologin">
        <ion-item class="login-item">
          <ion-label color="Contraste" position="floating" for="username">Usuario</ion-label>
          <ion-input id="username" autofocus="on" clearInput="true" clearOnEdit="true" inputmode="text" placeholder="Ingrese su Usuario" required="true" formControlName="user" color="Contraste" ></ion-input>
          <ion-icon name="person" color="Contraste" slot="start"></ion-icon>
        </ion-item>

        <ion-item class="login-item">
          <ion-label color="Contraste" position="floating" for="password">Contraseña</ion-label>
          <ion-input id="password" placeholder="Ingrese su Contraseña" required="true" formControlName="password" type="password" color="Contraste" ></ion-input>
          <ion-icon name="lock-closed" color="Contraste" slot="start"></ion-icon>
        </ion-item>
        
      </form>

      <br>

      <ion-row class="login-row"></ion-row>

      <!-- <ion-item><ion-label><hr class="solid"></ion-label></ion-item> -->

      <ion-button  class="login-button" expand="block"  (click)="ingresar()">
        Ingresar
      </ion-button>

      <ion-row class="login-row"></ion-row>

      <h3 class="login-header">¿Desea crear una cuenta?</h3>

      <ion-button shape="round" color="dark" expand="block" [routerLink]="['/rpaciente']">
        Crear cuenta como Paciente
      </ion-button>

      <ion-row class="login-row"></ion-row>

      <ion-button shape="round" color="dark" expand="block" [routerLink]="['/rdoctor']">
        Crear cuenta como Doctor
      </ion-button>
    </ion-card-content>
  </ion-card>
</ion-content>
