import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { Router } from '@angular/router';
import { AlertController } from '@ionic/angular';
import { especialidad } from 'src/app/models/interface';
import { AuthService } from 'src/app/services/auth.service';
import { FirestoreService } from 'src/app/services/firestore.service';

@Component({
  selector: "app-c-especialidad",
  templateUrl: "./c-especialidad.page.html",
  styleUrls: ["./c-especialidad.page.scss"],
})
export class CEspecialidadPage implements OnInit {
  public especialidadform: FormGroup;

  uid: any;
  info: especialidad | undefined;
  newespecialidad: especialidad = {
    especialidad: "",
    id: this.store.getId(),
  };

  constructor(
    private fb: FormBuilder,
    private auth: AuthService,
    private router: Router,
    private store: FirestoreService,
    private alertcontroller: AlertController
  ) {}

  ngOnInit() {}
}
<ion-header>
  <ion-toolbar color="principal">
    <ion-title>Especialidad</ion-title>
    <ion-button color="blanco" fill="clear" slot="start" [routerLink]="['/homea']"><ion-icon
        name="arrow-back-outline"></ion-icon>
    </ion-button>
  </ion-toolbar>
</ion-header>

<ion-content>

  <div class="container">
    <h1 class="titulo">Ingresar Especialidad</h1>
    <form [formGroup]="especialidadform" (ngSubmit)="ingresar()" class="formulario">
      <ion-item>
        <ion-label position="stacked">Medicamento <ion-text color="danger">*</ion-text></ion-label>
        <ion-input required formControlName="especialidad" type="text"></ion-input>
      </ion-item>
      <div class="botones">
        <ion-button color="secondary" expand="block" type="submit">
          Aceptar
        </ion-button>
        <ion-button color="light" expand="block" type="reset">
          Cancelar
        </ion-button>
      </div>
    </form>
  </div>

</ion-content>
