import { Component, OnInit } from "@angular/core";
import {
  FormBuilder,
  FormControl,
  FormGroup,
  Validators,
} from "@angular/forms";
import { Router } from "@angular/router";
import { AlertController } from "@ionic/angular";
import { cita, doctor, paciente } from "src/app/models/interface";
import { AuthService } from "src/app/services/auth.service";
import { FirestoreService } from "src/app/services/firestore.service";

@Component({
  selector: "app-a-citas",
  templateUrl: "./a-citas.page.html",
  styleUrls: ["./a-citas.page.scss"],
})
export class ACitasPage implements OnInit {
  public acitasform: FormGroup;

  pathpaciente = "Pacientes";
  pathdoctor = "Doctores";
  uidpaciente: any;
  uiddoctor: any;
  infodoctor: doctor[] = [];
  doctores: doctor[] = [];
  pacientes: paciente[] = [];

  doctor: doctor = {
    uid: "",
    nombre: "",
    apellido: "",
    cedula: "",
    especialidad: "",
    correo: "",
    contraseña: "",
    rol: "Doctor",
  };
  paciente: paciente = {
    uid: "",
    nombre: "",
    apellido: "",
    cedula: "",
    fnacimiento: "",
    edad: 0,
    correo: "",
    contraseña: "",
    rol: "Paciente",
  };

  newcita: cita = {
    doctor: "",
    iddoctor: "",
    paciente: "",
    idpaciente: "",
    especialidad: "",
    fecha: new Date(),
    hora: 0,
    id: this.store.getId()
  };

  constructor(
    private fb: FormBuilder,
    private auth: AuthService,
    private router: Router,
    private store: FirestoreService,
    private alertcontroller: AlertController
  ) {
    this.acitasform = this.fb.group({
      doctor: new FormControl("", Validators.required),
      fecha: new FormControl("", Validators.required),
      hora: new FormControl("", Validators.required),
    });
  }

  ngOnInit() {
    this.getdoctores();
  }

  getdoctores() {
    this.store.consultar<doctor>(this.pathdoctor).subscribe((res) => {
      if (res) {
        this.infodoctor = res;
      }
    });
  }

  async agregar() {
    var f = this.acitasform.value;
    const uid = await this.auth.getUid();

    let citaCreada = false; 

    this.uiddoctor = f.doctor;

    this.store.consultar<doctor>(this.pathdoctor).subscribe((res) => {
      if (res) {
        this.doctores = res;
        this.doctores.forEach(async (docs) => {
          if (docs.uid == this.uiddoctor) {
            this.doctor = docs;
            if (uid) {
              this.uidpaciente = uid;
              this.store
                .consultar<paciente>(this.pathpaciente)
                .subscribe((pac) => {
                  if (pac) {
                    this.pacientes = pac;
                    this.pacientes.forEach(async (pac2) => {
                      if (pac2.uid == this.uidpaciente) {
                        this.paciente = pac2;
                        if (this.acitasform.invalid) {
                          const alert = await this.alertcontroller.create({
                            header: "Datos incompletos",
                            message: "Debe de ingresar todos los campos",
                          });

                          await alert.present();
                          return;
                        } else {
                          if (!citaCreada) {
                            this.newcita.doctor = this.doctor.nombre + " " + this.doctor.apellido;
                            this.newcita.especialidad = this.doctor.especialidad;
                            this.newcita.fecha = f.fecha;
                            this.newcita.hora = f.hora;
                            this.newcita.iddoctor = this.doctor.uid;
                            this.newcita.idpaciente = this.uidpaciente;
                            this.newcita.paciente = this.paciente.nombre + " " + this.paciente.apellido;
                            this.newcita.id = this.store.getId()

                            console.log(this.newcita);

                            this.store.createDoc(this.newcita, "Citas", this.newcita.id);

                            citaCreada = true; 

                            const alert2 = await this.alertcontroller.create({
                              header: "Cita agregada",
                            });

                            await alert2.present();
                            this.router.navigate(["../home"]);
                          }
                        }
                      }
                    });
                  }
                });
            }
          }
        });
      }
    });
  }
}
<ion-header>
  <ion-toolbar color="principal">
    <ion-title>Agendar Citas</ion-title>
    <ion-button color="blanco" fill="clear" slot="start" [routerLink]="['/home']"><ion-icon name="arrow-back-outline"></ion-icon>
    </ion-button>
  </ion-toolbar>
</ion-header>

<ion-content>

  <form [formGroup]="acitasform" (ngSubmit)="agregar()">
    <ion-item>
      <ion-label position="floating">Doctor <ion-text color="danger">*</ion-text></ion-label>
      <ion-select formControlName="doctor" interface="popover" placeholder="Selecciona Doctor">
        <ion-select-option *ngFor="let doctor of infodoctor" [value]="doctor.uid">{{doctor.nombre}}</ion-select-option>
      </ion-select>
    </ion-item>
    <ion-item>
      <ion-label position="floating">Fecha <ion-text color="danger">*</ion-text></ion-label>
      <ion-input required value="number" formControlName="fecha" type="date"></ion-input>
    </ion-item>  
    <ion-item>
      <ion-label position="floating">Hora <ion-text color="danger">*</ion-text></ion-label>
      <ion-input required value="number" formControlName="hora" type="time"></ion-input>
    </ion-item>
    <ion-button block color="secundario" type="submit">
      Aceptar
    </ion-button>
  </form>

</ion-content>
